import React from 'react';


const btnSubmit = {
    margin: '20px 30px 20px 0',
    position: 'relatif',
    backgroundColor: 'green'
}

function ButtonSubmit() {
    const classes = useStyles();
    // getModalStyle is not a pure function, we roll the style only on the first
    // render
    const [modalStyle] = React.useState(getModalStyle);
    const [open, setOpen] = React.useState(false);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const body = (
        <div style={modalStyle} className={classes.paper}>
            <h2 id="simple-modal-title">Perhatian !!!</h2>
            <input
                className={classes.input}
                id="contained-button-file"
                multiple="multiple"
                type="file" />
            <label htmlFor="contained-button-file">
                <Button
                    startIcon={<CloudUploadIcon />}
                    variant="contained"
                    color="primary" d
                    component="span">
                    Upload
                </Button >
                <Button >
                    Submit
                </Button>
            </label>
            <p id="simple-modal-description">
                Sebelum melakukan Import Data Excel pastikan Format Data Excel sudah sama dengan
                template
            </p>
            <ReactExcel />

        </div>
    );

    return (

        <div>
            <button style={btnSubmit} type="button" onClick={handleOpen}>Submit Data</button>
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description">
                {body}
            </Modal>
        </div>
    );
}
export default ButtonSubmit;
